import { userRouter, useRouter } from 'next/router';
import Layout from '../comps/MyLayout';

const Content = () => {
    const router = useRouter();
    return (
        <>
            <h1>{router.query.title}</h1>
            <p>This is the blog post content</p>
        </>
    );
};

const Page = () => (
    <Layout><Content /></Layout>
);

export default Page;
